<script type="text/javascript">
    var $function_after_location_set = get_listing;
    var $page_no = 1;
    $(function(){
        $(document).on("click",".lt-control-search-mobile-close,.lt-control-search-mobile",function(){
           $(".lt-listing-filters").toggleClass("open");
        });
        $(document).on("click","#load_more_listing",function(){
           get_all_records();
        });
    });
    function get_listing() {
       $page_no = 1; 
       get_all_records();
    }
    function get_all_records() {
        if($page_no ==1 ) {
            var $url = "<?php echo base_url("listing/get_all_listing"); ?>";
        }else{
            var $url = "<?php echo base_url("listing/get_all_listing/"); ?>"+$page_no;
        }
        commonAjx($url, "","lt-listing-filters","","post","",set_listing_response);
    }
    function set_listing_response($response){
        var $resp = JSON.parse($response);
        if( $page_no==1 ) {
            $("#listing_result").html($resp.data);
        }else{
            $("#listing_result").append($resp.data);
        }
        if( $page_no>=$resp.total_pages ) {
            $("#load_more_listing").addClass("d-none");
        }
        $page_no++;
    }
    
</script>